import { Link, useLocation, useNavigate } from 'react-router-dom';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import { Helmet } from 'react-helmet-async';
import axios from 'axios';
import { useState} from 'react';


export default function LoginView() {
  const navigate = useNavigate();
  const { search } = useLocation();
  const redirectInUrl = new URLSearchParams(search).get('redirect');
  const redirect = redirectInUrl ? redirectInUrl : '/';
  
  const[name, setName] = useState('');
  const[user, setUser] = useState('');
  const[password, setPassword] = useState('');
  const[confirmPassword, setConfirmPassword] = useState('');



  const submit = async (e) => {
    e.preventDefault();
    try {
      const { data } = await axios.post('/api/users/signup', {
        user,
        password,
      });
      localStorage.setItem('userInfo', JSON.stringify(data));
      navigate(redirect || '/');

    } catch (error) {
      alert("Usuário ou senha incorretos!")
    }
  }

  return (
    <Container className="small-container">
      <Helmet>
        <title>Cadastro de Usuário</title>
      </Helmet>
      <h1 className="my-3">Cadastro</h1>
      <Form onSubmit={submit}>
        <Form.Group className="mb-3" controlId="user">
          <Form.Label>Nome</Form.Label>
          <Form.Control type="text" required onChange={(e) => setName(e.target.value)}/>
        </Form.Group>
        <Form.Group className="mb-3" controlId="user">
          <Form.Label>Usuário</Form.Label>
          <Form.Control type="text" required onChange={(e) => setUser(e.target.value)}/>
        </Form.Group>
        <Form.Group className="mb-3" controlId="password">
          <Form.Label>Senha</Form.Label>
          <Form.Control type="password" required onChange={(e) => setPassword(e.target.value)}/>
        </Form.Group>
        <Form.Group className="mb-3" controlId="password">
          <Form.Label>Confirme a Senha</Form.Label>
          <Form.Control type="password" required onChange={(e) => setConfirmPassword(e.target.value)}/>
        </Form.Group>
        <div className="mb-3">
          <Button variant="warning" type="submit">Enviar</Button>
        </div>
        <div className="mb-3">
          <Link to={`/Logged?redirect=${redirect}`}>Já possuo uma conta</Link>
        </div>
      </Form>
    </Container>
  );
}
